mapper.select:
SELECT * FROM ( SELECT p.*, COALESCE(c.comment_count, 0) AS comment_count, ROW_NUMBER() OVER (
ORDER BY p.post_date DESC ORDER BY (p.post_like - p.post_dislike + COALESCE(c.comment_count, 0)) DESC ORDER BY p.hit DESC
) AS rn FROM POSTS p LEFT JOIN ( SELECT post_id, COUNT(*) AS comment_count FROM COMMENTS GROUP BY post_id ) c ON p.post_id = c.post_id ) WHERE rn BETWEEN #{params.offset} AND #{params.offset} + #{params.limit} - 1

SELECT * FROM ( SELECT p.*, ROW_NUMBER() OVER (
ORDER BY p.post_date DESC ORDER BY (p.post_like - p.post_dislike + COALESCE(c.comment_count, 0)) DESC ORDER BY p.hit DESC
) AS rn FROM POSTS p LEFT JOIN ( SELECT post_id, COUNT(*) AS comment_count FROM COMMENTS GROUP BY post_id ) c ON p.post_id = c.post_id WHERE p.category = '공지' ) WHERE rn BETWEEN #{params.offset} AND #{params.offset} + #{params.limit} - 1

SELECT * FROM ( SELECT p.*, ROW_NUMBER() OVER (
ORDER BY p.post_date DESC ORDER BY (p.post_like - p.post_dislike + COALESCE(c.comment_count, 0)) DESC ORDER BY p.hit DESC
) AS rn FROM POSTS p LEFT JOIN ( SELECT post_id, COUNT(*) AS comment_count FROM COMMENTS GROUP BY post_id ) c ON p.post_id = c.post_id WHERE p.category = '축구' ) WHERE rn BETWEEN #{params.offset} AND #{params.offset} + #{params.limit} - 1

SELECT * FROM ( SELECT p.*, ROW_NUMBER() OVER (
ORDER BY p.post_date DESC ORDER BY (p.post_like - p.post_dislike + COALESCE(c.comment_count, 0)) DESC ORDER BY p.hit DESC
) AS rn FROM POSTS p LEFT JOIN ( SELECT post_id, COUNT(*) AS comment_count FROM COMMENTS GROUP BY post_id ) c ON p.post_id = c.post_id WHERE p.category = '야구' ) WHERE rn BETWEEN #{params.offset} AND #{params.offset} + #{params.limit} - 1

SELECT * FROM ( SELECT p.*, ROW_NUMBER() OVER (
ORDER BY p.post_date DESC ORDER BY (p.post_like - p.post_dislike + COALESCE(c.comment_count, 0)) DESC ORDER BY p.hit DESC
) AS rn FROM POSTS p LEFT JOIN ( SELECT post_id, COUNT(*) AS comment_count FROM COMMENTS GROUP BY post_id ) c ON p.post_id = c.post_id WHERE p.category = '농구' ) WHERE rn BETWEEN #{params.offset} AND #{params.offset} + #{params.limit} - 1

mapper.delete:
DELETE FROM POSTS
WHERE post_id = #{postId} 
AND (user_id = #{userId} OR #{isAdmin} = 'true')

DELETE FROM COMMENTS
WHERE comment_id = #{commentId} 
AND (user_id = #{userId} OR #{isAdmin} = 'true')

mapper.select:
SELECT * FROM ( SELECT p.*, COALESCE(c.comment_count, 0) AS comment_count, ROW_NUMBER() OVER (
ORDER BY p.post_date DESC ORDER BY (p.post_like - p.post_dislike + COALESCE(c.comment_count, 0)) DESC ORDER BY p.hit DESC
) AS rn FROM POSTS p LEFT JOIN ( SELECT post_id, COUNT(*) AS comment_count FROM COMMENTS GROUP BY post_id ) c ON p.post_id = c.post_id WHERE p.post_title LIKE '%' || #{searchTerm} || '%' ) WHERE rn BETWEEN #{offset} AND #{offset} + #{limit} - 1

mapper.update:
UPDATE POSTS
SET post_like = post_like + 1
WHERE post_id = #{postId}

UPDATE POSTS
SET post_dislike = post_dislike + 1
WHERE post_id = #{postId}

UPDATE COMMENTS
SET comment_like = comment_like + 1
WHERE comment_id = #{commentId}

UPDATE COMMENTS
SET comment_dislike = comment_dislike + 1
WHERE comment_id = #{commentId}

mapper.select:
SELECT COUNT(*) 
FROM POST_LIKES_DISLIKES 
WHERE user_id = #{userId} AND post_id = #{postId}

SELECT COUNT(*) 
FROM COMMENT_LIKES_DISLIKES 
WHERE user_id = #{userId} AND comment_id = #{commentId}

mapper.update:
UPDATE POSTS
SET HIT = HIT + 1
WHERE post_id = #{postId}
